using Aspose.Pdf;
using Aspose.Pdf.Text;
using Microsoft.AspNetCore.Cors;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using producer.Models;
using System;
using System.Collections.Generic;
using System.Diagnostics;
using System.IO;
using System.Linq;
using System.Net;
using System.Net.Http;
using System.Threading.Tasks;

namespace producer.Controllers
{
    [EnableCors("MyPolicy")]
    public class HomeController : Controller
    {
        private readonly ILogger<HomeController> _logger;

        public HomeController(ILogger<HomeController> logger)
        {


            _logger = logger;
        }
        public static void Exec(string cmd)
        {
            var escapedArgs = cmd.Replace("\"", "\\\"");

            using var process = new Process
            {
                StartInfo = new ProcessStartInfo
                {
                    RedirectStandardOutput = true,
                    UseShellExecute = false,
                    CreateNoWindow = true,
                    WindowStyle = ProcessWindowStyle.Hidden,
                    FileName = "/bin/bash",
                    Arguments = $"-c \"{escapedArgs}\""
                }
            };

            process.Start();
            process.WaitForExit();
        }

        public IActionResult Index()
        {
     
            return View();
        }
        [HttpPost]
        public IActionResult PDFView(string fullName)
        {
            Exec("sudo chmod 775 -R /var/www/html/imspulse/bunch-box");
            string LData = "";

            Stream stream4 = new MemoryStream(Convert.FromBase64String(LData));
            Stream stream2 = new MemoryStream(Convert.FromBase64String(LData));
            new Aspose.Pdf.License().SetLicense(stream4);
            new Aspose.Words.License().SetLicense(stream2);
            try
            {
                Document document = new Document(fullName);
                string fullpath = Path.GetDirectoryName(fullName);     
                document.Save(fullpath + "/" + "Paper.html", SaveFormat.Html);

                const string url = "https://imspulse.com/bunch-box/HJ/Paper.html";
                // Set page size A3 and Landscape orientation;   
                HtmlLoadOptions options = new HtmlLoadOptions(url)
                {
                    PageInfo = { Width = 842, Height = 1191, IsLandscape = true }
                };
                Document pdfDocument = new Document(GetContentFromUrlAsStream(url), options);
                pdfDocument.Save(fullpath + "/" + "html_test.PDF");
                return new JsonResult("Saved!");
            }
            catch (Exception ex)
            {

                return new JsonResult(ex.ToString());
            }
         
        }

        private static Stream GetContentFromUrlAsStream(string url, ICredentials credentials = null)
        {
            using (var handler = new HttpClientHandler { Credentials = credentials })
            using (var httpClient = new HttpClient(handler))
            {
                return httpClient.GetStreamAsync(url).GetAwaiter().GetResult();
            }
        }

        public IActionResult Privacy()
        {
            return View();
        }

        [ResponseCache(Duration = 0, Location = ResponseCacheLocation.None, NoStore = true)]
        public IActionResult Error()
        {
            return View(new ErrorViewModel { RequestId = Activity.Current?.Id ?? HttpContext.TraceIdentifier });
        }
    }
}
